<?php/** * @var core\View $this */?><ul id="menu">    <?php        $menu_pages = ['index', 'sites', 'templates', 'apps', 'skins'];        foreach ($menu_pages as $menu_page) {            $isActive = $menu_page == $this->app->page->alias;            $menu_page_url = $this->app->createUrl($menu_page);            $menu_page_title = $this->app->pages[$menu_page]['title'];            echo $isActive ? '<li class="current">' : '<li>';            echo lib\Url::createLink($menu_page_url, $menu_page_title, $isActive);            echo '</li>';        }    ?></ul>